extern void slurmscriptd_free_msg ( slurmscriptd_msg_t * msg ) { if ( ! msg ) { return ; } switch ( msg -> msg_type ) { case SLURMSCRIPTD_REQUEST_RECONFIG : slurmscriptd_free_reconfig ( msg -> msg_data ) ; break ; case SLURMSCRIPTD_REQUEST_RUN_SCRIPT : slurmscriptd_free_run_script_msg ( msg -> msg_data ) ; break ; case SLURMSCRIPTD_REQUEST_SCRIPT_COMPLETE : break ; default : xfree ( msg -> msg_data ) ; break ; } xfree ( msg -> key ) ; } 