static int evaluate_symbol_call ( struct expression * expr ) { struct expression * fn = expr -> fn ; struct symbol * ctype = fn -> ctype ; if ( fn -> type != EXPR_PREOP ) { return 0 ; } if ( ctype -> ctype . modifiers & MOD_INLINE ) { int ret ; struct symbol * curr = current_fn ; if ( ctype -> definition ) { ctype = ctype -> definition ; } current_fn = ctype -> ctype . base_type ; ret = inline_function ( expr , ctype ) ; current_fn = curr ; return ret ; } return 0 ; } 