static enum bp_result bios_parser_get_device_tag ( struct dc_bios * dcb , struct graphics_object_id connector_object_id , uint32_t device_tag_index , struct connector_device_tag_info * info ) { struct bios_parser * bp = BP_FROM_DCB ( dcb ) ; ATOM_OBJECT * object ; ATOM_CONNECTOR_DEVICE_TAG_RECORD * record = NULL ; ATOM_CONNECTOR_DEVICE_TAG * device_tag ; if ( ! info ) { return BP_RESULT_BADINPUT ; } object = get_bios_object ( bp , connector_object_id ) ; if ( ! object ) { BREAK_TO_DEBUGGER ( ) ; return BP_RESULT_BADINPUT ; } if ( bios_parser_get_device_tag_record ( bp , object , & record ) != BP_RESULT_OK ) { return BP_RESULT_NORECORD ; } if ( device_tag_index >= record -> ucNumberOfDevice ) { return BP_RESULT_NORECORD ; } device_tag = & record -> asDeviceTag [ device_tag_index ] ; info -> acpi_device = le32_to_cpu ( device_tag -> ulACPIDeviceEnum , NULL ) ; info -> dev_id = device_type_from_device_id ( le16_to_cpu ( device_tag -> usDeviceID ) ) ; return BP_RESULT_OK ; } 