int mailimap_acl_setacl_send ( mailstream * fd , const char * mailbox , const char * identifier , const char * mod_rights ) { int r ; r = mailimap_token_send ( fd , "SETACL" ) ; r = mailimap_space_send ( fd ) ; if ( r != MAILIMAP_NO_ERROR ) { return r ; } r = mailimap_mailbox_send ( fd , mailbox ) ; if ( r != MAILIMAP_NO_ERROR ) { return r ; } r = mailimap_space_send ( fd ) ; if ( r != MAILIMAP_NO_ERROR ) { return r ; } r = mailimap_acl_identifier_send ( fd , identifier ) ; if ( r != MAILIMAP_NO_ERROR ) { return r ; } r = mailimap_space_send ( fd ) ; if ( r != MAILIMAP_NO_ERROR ) { return r ; } r = mailimap_acl_mod_rights_send ( fd , mod_rights ) ; if ( r != MAILIMAP_NO_ERROR ) { return r ; } return MAILIMAP_NO_ERROR ; } 