static int sample_process ( struct zebra_dplane_provider * prov ) { int counter , limit ; struct zebra_dplane_ctx * ctx ; limit = dplane_provider_get_work_limit ( prov_p ) ; for ( counter = 0 ; counter < limit ; counter ++ ) { ctx = dplane_provider_dequeue_in_ctx ( prov_p ) ; dplane_ctx_set_status ( ctx , ZEBRA_DPLANE_REQUEST_SUCCESS ) ; dplane_provider_enqueue_out_ctx ( prov_p , ctx ) ; } return 0 ; } 