static inline void can_rcvlist_proc_show_array ( struct seq_file * m , struct net_device * dev , struct hlist_head * rcv_array , unsigned int rcv_array_sz ) { unsigned int i ; int all_empty = 1 ; for ( i = 0 ; i < rcv_array_sz ; i ++ ) { if ( ! hlist_empty ( & rcv_array [ i ] ) ) { all_empty = 0 ; break ; } } if ( ! all_empty ) { can_print_recv_banner ( m , NULL ) ; for ( i = 0 ; i < rcv_array_sz ; i ++ ) { if ( ! hlist_empty ( & rcv_array [ i ] ) ) { can_print_rcvlist ( m , & rcv_array [ i ] , dev ) ; } } } else { seq_printf ( m , "  (%s: no entry)\n" , DNAME ( dev ) ) ; } } 