int xfs_buf_read_uncached ( struct xfs_buftarg * target , xfs_daddr_t daddr , size_t numblks , int flags , struct xfs_buf * * bpp , const struct xfs_buf_ops * ops ) { struct xfs_buf * bp ; * bpp = NULL ; bp = xfs_buf_get_uncached ( target , numblks , flags ) ; if ( ! bp ) { return - ENOMEM ; } ASSERT ( bp -> b_map_count == 1 ) ; bp -> b_bn = XFS_BUF_DADDR_NULL ; bp -> b_maps [ 0 ] . bm_bn = daddr ; bp -> b_flags |= XBF_READ ; bp -> b_ops = ops ; xfs_buf_submit_wait ( bp ) ; if ( bp -> b_error ) { int error = bp -> b_error ; xfs_buf_relse ( bp , NULL ) ; return error ; } * bpp = bp ; return 0 ; } 