static int jz4740_mmc_remove ( struct platform_device * pdev ) { struct jz4740_mmc_host * host = platform_get_drvdata ( pdev ) ; del_timer_sync ( & host -> timeout_timer ) ; jz4740_mmc_set_irq_enabled ( host , 0xff , false ) ; jz4740_mmc_reset ( host , NULL ) ; mmc_remove_host ( host -> mmc ) ; free_irq ( host -> irq , host ) ; jz4740_mmc_free_gpios ( pdev ) ; jz_gpio_bulk_free ( jz4740_mmc_pins , jz4740_mmc_num_pins ( host ) ) ; if ( host -> use_dma ) { jz4740_mmc_release_dma_channels ( host ) ; } mmc_free_host ( host -> mmc ) ; return 0 ; } 