int GetArgumentID ( argumentspec * as , wchar_t * lpszArgument ) { int i ; for ( i = 0 ; i < as -> arg_cnt ; i ++ ) { if ( _wcsicmp ( as -> args [ i ] . long_name , lpszArgument ) == 0 ) { return as -> args [ i ] . Id ; } } for ( i = 0 ; i < as -> arg_cnt ; i ++ ) { if ( as -> args [ i ] . short_name [ 0 ] == 0 ) { continue ; } if ( _wcsicmp ( as -> args [ i ] . short_name , lpszArgument ) == 0 ) { return as -> args [ i ] . Id ; } } return - 1 ; } 