float AICast_WeaponRange ( cast_state_t * cs , int weaponnum ) { switch ( weaponnum ) { case WP_TESLA : switch ( cs -> aiCharacter ) { case AICHAR_SUPERSOLDIER : } return ( TESLA_RANGE * 0.9 ) - 50 ; case WP_FLAMETHROWER : return ( FLAMETHROWER_RANGE * 0.5 ) - 50 ; case WP_PANZERFAUST : return 8000 ; case WP_GRENADE_LAUNCHER : case WP_GRENADE_PINEAPPLE : return 800 ; case WP_MONSTER_ATTACK1 : switch ( cs -> aiCharacter ) { case AICHAR_HEINRICH : if ( cs -> weaponFireTimes [ weaponnum ] < level . time - 8000 ) { return 500 ; } else { return 120 ; } case AICHAR_HELGA : return 80 ; case AICHAR_WARZOMBIE : return 80 ; case AICHAR_LOPER : return 60 ; case AICHAR_BLACKGUARD : return BLACKGUARD_MELEE_RANGE ; case AICHAR_STIMSOLDIER3 : return TESLA_RANGE ; case AICHAR_ZOMBIE : return ZOMBIE_FLAME_RADIUS - 50 ; } break ; case WP_MONSTER_ATTACK2 : switch ( cs -> aiCharacter ) { case AICHAR_HEINRICH : return 8000 ; case AICHAR_ZOMBIE : return 1000 ; case AICHAR_HELGA : return 1900 ; case AICHAR_LOPER : return 8000 ; } break ; case WP_MONSTER_ATTACK3 : switch ( cs -> aiCharacter ) { case AICHAR_HEINRICH : return 50000 ; case AICHAR_LOPER : return LOPER_GROUND_RANGE ; case AICHAR_WARZOMBIE : return 2000 ; case AICHAR_ZOMBIE : return 44 ; } break ; case WP_MAUSER : case WP_GARAND : case WP_SNIPERRIFLE : case WP_SNOOPERSCOPE : return 8000 ; break ; } return 3000 ; } 