int dump_queue ( struct msgque_data * msgque ) { struct msqid_ds ds ; int kern_id ; int i , ret ; for ( kern_id = 0 ; kern_id < 256 ; kern_id ++ ) { ret = msgctl ( kern_id , MSG_STAT , & ds ) ; if ( ret < 0 ) { printf ( "Failed to get stats for IPC queue with id %d\n" , kern_id ) ; return - errno ; } if ( ret == msgque -> msq_id ) { break ; } } msgque -> messages = malloc ( sizeof ( msg1 ) * ds . msg_qnum ) ; if ( msgque -> messages == NULL ) { printf ( "Failed to get stats for IPC queue\n" ) ; return - ENOMEM ; } msgque -> qnum = ds . msg_qnum ; msgque -> mode = ds . msg_perm . mode ; msgque -> qbytes = ds . msg_qbytes ; for ( i = 0 ; i < msgque -> qnum ; i ++ ) { ret = msgrcv ( msgque -> msq_id , & msgque -> messages [ i ] . mtype , MAX_MSG_SIZE , i , IPC_NOWAIT | MSG_COPY ) ; if ( ret < 0 ) { printf ( "Failed to copy IPC message: %m (%d)\n" , errno ) ; return - errno ; } msgque -> messages [ i ] . msize = ret ; } return 0 ; } 