static bool pmc_core_pri_verify ( u32 lpm_pri , u8 * mode_order ) { int i , j ; if ( ! lpm_pri ) { return false ; } for ( i = 0 ; i < 8 ; i ++ ) { int level = lpm_pri & GENMASK ( 3 , 0 ) ; mode_order [ i ] = level ; lpm_pri >>= 4 ; } for ( i = 0 ; i < LPM_MAX_NUM_MODES - 1 ; i ++ ) { for ( j = i + 1 ; j < LPM_MAX_NUM_MODES ; j ++ ) { if ( mode_order [ i ] == mode_order [ j ] ) { return false ; } } } return true ; } 