static int check_host ( nss_XbyY_args_t * argp , const char * line , int linelen ) { const char * limit , * linep , * keyp ; linep = line ; limit = line + linelen ; while ( linep < limit && isspace ( * linep ) ) { linep ++ ; } while ( linep < limit && ! isspace ( * linep ) ) { linep ++ ; } while ( linep < limit && isspace ( * linep ) ) { linep ++ ; } keyp = argp -> key . name ; while ( * keyp != '\0' && linep < limit && * keyp == * linep ) { keyp ++ ; linep ++ ; } return ( * keyp == '\0' && linep == limit ) ; } 