static int checkTPGList ( MP_UINT32 tpgID , MP_UINT64 objectSequenceNumber ) { int tpg = 0 ; int status = MP_FALSE ; MP_OID luOID ; MP_OID_LIST * ppList = NULL ; MP_STATUS mpStatus = MP_STATUS_SUCCESS ; MP_TARGET_PORT_GROUP_PROPERTIES tpgProps ; log ( LOG_INFO , "checkTPGList()" , " - enter" ) ; luOID . objectSequenceNumber = objectSequenceNumber ; luOID . objectType = MP_OBJECT_TYPE_MULTIPATH_LU ; luOID . ownerId = g_pluginOwnerID ; mpStatus = getAssociatedTPGOidList ( luOID , & ppList ) ; if ( MP_STATUS_SUCCESS != mpStatus ) { log ( LOG_INFO , "checkTPGList()" , " - getAssociatedTPGOidList() failed: %d" , mpStatus ) ; return ( MP_FALSE ) ; } for ( tpg = 0 ; tpg < ppList -> oidCount ; tpg ++ ) { mpStatus = getTargetPortGroupProperties ( ppList -> oids [ tpg ] , & tpgProps ) ; if ( MP_STATUS_SUCCESS != mpStatus ) { log ( LOG_INFO , "checkTPGList()" , " - getTargetPortGroupProperties() failed: %d" , mpStatus ) ; return ( MP_FALSE ) ; } if ( tpgProps . tpgID == tpgID ) { status = MP_TRUE ; log ( LOG_INFO , "checkTPGList()" , " - found a match" ) ; break ; } } log ( LOG_INFO , "checkTPGList()" , " - exit" ) ; return ( status ) ; } 