static int adxrs450_probe ( struct spi_device * spi ) { int ret ; struct adxrs450_state * st ; struct iio_dev * indio_dev ; indio_dev = devm_iio_device_alloc ( & spi -> dev , sizeof ( * st ) ) ; if ( ! indio_dev ) { return - ENOMEM ; } st = iio_priv ( indio_dev ) ; st -> us = spi ; mutex_init ( & st -> buf_lock ) ; spi_set_drvdata ( spi , indio_dev ) ; indio_dev -> dev . parent = & spi -> dev ; indio_dev -> info = & adxrs450_info ; indio_dev -> modes = INDIO_DIRECT_MODE ; indio_dev -> channels = adxrs450_channels [ spi_get_device_id ( spi ) -> driver_data ] ; indio_dev -> num_channels = ARRAY_SIZE ( adxrs450_channels , NULL ) ; indio_dev -> name = spi -> dev . driver -> name ; ret = devm_iio_device_register ( & spi -> dev , indio_dev ) ; if ( ret ) { return ret ; } ret = adxrs450_initial_setup ( indio_dev ) ; if ( ret ) { return ret ; } return 0 ; } 