void child_update ( void ) { pr_child_t * ch , * chn = NULL ; if ( child_list == NULL ) { return ; } for ( ch = ( pr_child_t * ) child_list -> xas_list ; ch ; ch = chn ) { chn = ch -> next ; if ( ch -> ch_dead ) { if ( ch -> ch_pipefd != - 1 ) { ( void ) close ( ch -> ch_pipefd ) ; } xaset_remove ( child_list , ( xasetmember_t * ) ch ) ; } } if ( child_list -> xas_list == NULL ) { destroy_pool ( child_list -> pool ) ; child_list = NULL ; child_listlen = 0 ; } } 