static void sfe_rx_desc_write ( struct gem_dev * dp , int slot , ddi_dma_cookie_t * dmacookie , int frags ) { struct sfe_desc * rdp ; uint32_t tmp0 ; uint32_t tmp1 ; int i ; ASSERT ( frags == 1 ) ; cmn_err ( CE_CONT , CONS "%s: %s seqnum: %d, slot %d, frags: %d" , dp -> name , __func__ , dp -> rx_active_tail , slot , frags ) ; for ( i = 0 ; i < frags ; i ++ ) { cmn_err ( CE_CONT , CONS "  frag: %d addr: 0x%llx, len: 0x%lx" , i , dmacookie [ i ] . dmac_address , dmacookie [ i ] . dmac_size ) ; } rdp = ( void * ) & dp -> rx_ring [ SFE_DESC_SIZE * slot ] ; tmp0 = ( uint32_t ) dmacookie -> dmac_address ; tmp1 = CMDSTS_INTR | ( uint32_t ) dmacookie -> dmac_size ; rdp -> d_bufptr = LE_32 ( tmp0 ) ; rdp -> d_cmdsts = LE_32 ( tmp1 , NULL ) ; } 