static ssize_t stm32_tt_store_frequency ( struct device * dev , struct device_attribute * attr , const char * buf , size_t len ) { struct iio_trigger * trig = to_iio_trigger ( dev ) ; struct stm32_timer_trigger * priv = iio_trigger_get_drvdata ( trig ) ; unsigned int freq ; int ret ; ret = kstrtouint ( buf , 10 , & freq ) ; if ( freq == 0 ) { stm32_timer_stop ( priv ) ; } else { ret = stm32_timer_start ( priv , freq ) ; if ( ret ) { return ret ; } } return len ; } 