static int r9701_probe ( struct spi_device * spi ) { struct rtc_device * rtc ; struct rtc_time dt ; unsigned char tmp ; int res ; tmp = R100CNT ; res = read_regs ( & spi -> dev , & tmp , 1 ) ; if ( res || tmp != 0x20 ) { dev_err ( & spi -> dev , "cannot read RTC register\n" ) ; return - ENODEV ; } if ( r9701_get_datetime ( & spi -> dev , & dt ) ) { dev_info ( & spi -> dev , "trying to repair invalid date/time\n" ) ; dt . tm_sec = 0 ; dt . tm_min = 0 ; dt . tm_hour = 0 ; dt . tm_mon = 0 ; dt . tm_year = 100 ; if ( r9701_set_datetime ( & spi -> dev , & dt ) || r9701_get_datetime ( & spi -> dev , & dt ) ) { dev_err ( & spi -> dev , "cannot repair RTC register\n" ) ; return - ENODEV ; } } rtc = devm_rtc_device_register ( & spi -> dev , "r9701" , & r9701_rtc_ops , THIS_MODULE ) ; if ( IS_ERR ( rtc ) ) { return PTR_ERR ( rtc ) ; } spi_set_drvdata ( spi , rtc ) ; return 0 ; } 