int exynos_dpi_bind ( struct drm_device * dev , struct drm_encoder * encoder ) { int ret ; drm_simple_encoder_init ( dev , encoder , DRM_MODE_ENCODER_TMDS ) ; drm_encoder_helper_add ( encoder , & exynos_dpi_encoder_helper_funcs ) ; ret = exynos_drm_set_possible_crtcs ( encoder , EXYNOS_DISPLAY_TYPE_LCD ) ; if ( ret < 0 ) { return ret ; } ret = exynos_dpi_create_connector ( encoder ) ; if ( ret ) { DRM_DEV_ERROR ( encoder_to_dpi ( encoder ) -> dev , "failed to create connector ret = %d\n" , ret ) ; drm_encoder_cleanup ( encoder , NULL ) ; return ret ; } return 0 ; } 