static int my_parser ( struct lxc_arguments * args , int c , char * arg ) { switch ( c ) { case 'D' : checkpoint_dir = strdup ( arg ) ; if ( ! checkpoint_dir ) { return - 1 ; } break ; case 'A' : actionscript_path = strdup ( arg ) ; if ( ! actionscript_path ) { return - 1 ; } break ; case 's' : stop = true ; break ; case 'v' : verbose = true ; break ; case 'r' : do_restore = true ; break ; case 'd' : daemonize_set = true ; break ; case 'F' : args -> daemonize = 0 ; daemonize_set = true ; break ; case 'p' : pre_dump = true ; break ; case OPT_PREDUMP_DIR : predump_dir = strdup ( arg ) ; if ( ! predump_dir ) { return - 1 ; } break ; } return 0 ; } 