static uint64_t vgen_ldc_stat ( vgen_ldc_t * ldcp , uint_t stat ) { vgen_stats_t * statsp ; uint64_t val ; statsp = & ldcp -> stats ; switch ( stat ) { case MAC_STAT_MULTIRCV : val = statsp -> multircv ; break ; case MAC_STAT_BRDCSTRCV : val = statsp -> brdcstrcv ; break ; case MAC_STAT_MULTIXMT : val = statsp -> multixmt ; break ; case MAC_STAT_BRDCSTXMT : val = statsp -> brdcstxmt ; break ; case MAC_STAT_NORCVBUF : val = statsp -> norcvbuf ; break ; case MAC_STAT_IERRORS : val = statsp -> ierrors ; break ; case MAC_STAT_NOXMTBUF : val = statsp -> noxmtbuf ; break ; case MAC_STAT_OERRORS : val = statsp -> oerrors ; break ; case MAC_STAT_COLLISIONS : break ; case MAC_STAT_RBYTES : val = statsp -> rbytes ; break ; case MAC_STAT_IPACKETS : val = statsp -> ipackets ; break ; case MAC_STAT_OBYTES : val = statsp -> obytes ; break ; case MAC_STAT_OPACKETS : val = statsp -> opackets ; break ; case MAC_STAT_IFSPEED : case ETHER_STAT_ALIGN_ERRORS : case ETHER_STAT_FCS_ERRORS : case ETHER_STAT_FIRST_COLLISIONS : case ETHER_STAT_MULTI_COLLISIONS : case ETHER_STAT_DEFER_XMTS : case ETHER_STAT_TX_LATE_COLLISIONS : case ETHER_STAT_EX_COLLISIONS : case ETHER_STAT_MACXMT_ERRORS : case ETHER_STAT_CARRIER_ERRORS : case ETHER_STAT_TOOLONG_ERRORS : case ETHER_STAT_XCVR_ADDR : case ETHER_STAT_XCVR_ID : case ETHER_STAT_XCVR_INUSE : case ETHER_STAT_CAP_1000FDX : case ETHER_STAT_CAP_1000HDX : case ETHER_STAT_CAP_100FDX : case ETHER_STAT_CAP_100HDX : case ETHER_STAT_CAP_10FDX : case ETHER_STAT_CAP_10HDX : case ETHER_STAT_CAP_ASMPAUSE : case ETHER_STAT_CAP_PAUSE : case ETHER_STAT_CAP_AUTONEG : case ETHER_STAT_ADV_CAP_1000FDX : case ETHER_STAT_ADV_CAP_1000HDX : case ETHER_STAT_ADV_CAP_100FDX : case ETHER_STAT_ADV_CAP_100HDX : case ETHER_STAT_ADV_CAP_10FDX : case ETHER_STAT_ADV_CAP_10HDX : case ETHER_STAT_ADV_CAP_ASMPAUSE : case ETHER_STAT_ADV_CAP_PAUSE : case ETHER_STAT_ADV_CAP_AUTONEG : case ETHER_STAT_LP_CAP_1000FDX : case ETHER_STAT_LP_CAP_1000HDX : case ETHER_STAT_LP_CAP_100FDX : case ETHER_STAT_LP_CAP_100HDX : case ETHER_STAT_LP_CAP_10FDX : case ETHER_STAT_LP_CAP_10HDX : case ETHER_STAT_LP_CAP_ASMPAUSE : case ETHER_STAT_LP_CAP_PAUSE : case ETHER_STAT_LP_CAP_AUTONEG : case ETHER_STAT_LINK_ASMPAUSE : case ETHER_STAT_LINK_PAUSE : case ETHER_STAT_LINK_AUTONEG : case ETHER_STAT_LINK_DUPLEX : default : val = 0 ; break ; } return ( val ) ; } 