VOID VIOSerialEnableInterrupt ( PPORTS_DEVICE pContext ) { TraceEvents ( TRACE_LEVEL_INFORMATION , DBG_INTERRUPT , "-->%s enable\n" , __FUNCTION__ ) ; if ( ! pContext ) { return ; } if ( pContext -> c_ivq ) { virtqueue_enable_cb ( pContext -> c_ivq , NULL ) ; virtqueue_kick ( pContext -> c_ivq ) ; } TraceEvents ( TRACE_LEVEL_INFORMATION , DBG_INTERRUPT , "<-- %s enable\n" , __FUNCTION__ ) ; } 