static u32 ena_flow_hash_to_flow_type ( u16 hash_fields ) { int data = 0 ; if ( hash_fields & ENA_ADMIN_RSS_L2_DA ) { data |= RXH_L2DA ; } if ( hash_fields & ENA_ADMIN_RSS_L3_DA ) { data |= RXH_IP_DST ; } if ( hash_fields & ENA_ADMIN_RSS_L3_SA ) { data |= RXH_IP_SRC ; } if ( hash_fields & ENA_ADMIN_RSS_L4_DP ) { data |= RXH_L4_B_2_3 ; } if ( hash_fields & ENA_ADMIN_RSS_L4_SP ) { data |= RXH_L4_B_0_1 ; } return data ; } 