Node * transformLimitClause ( ParseState * pstate , Node * clause , ParseExprKind exprKind , const char * constructName , LimitOption limitOption ) { Node * qual ; qual = transformExpr ( pstate , clause , exprKind ) ; qual = coerce_to_specific_type ( pstate , qual , INT8OID , constructName ) ; checkExprIsVarFree ( pstate , qual , constructName ) ; if ( exprKind == EXPR_KIND_LIMIT && limitOption == LIMIT_OPTION_WITH_TIES && IsA ( clause , A_Const ) && castNode ( A_Const , clause ) -> isnull ) { ereport ( ERROR , ( errcode ( ERRCODE_INVALID_ROW_COUNT_IN_LIMIT_CLAUSE ) , errmsg ( "row count cannot be null in FETCH FIRST ... WITH TIES clause" ) ) ) ; } return qual ; } 