static int signal_fuzzer ( void ) { int t , rc ; pthread_t * threads ; threads = malloc ( nthread * sizeof ( pthread_t ) ) ; for ( t = 0 ; t < nthread ; t ++ ) { rc = pthread_create ( & threads [ t ] , NULL , sigfuz_test , ( void * ) & t ) ; if ( rc ) { perror ( "Thread creation error\n" ) ; } } for ( t = 0 ; t < nthread ; t ++ ) { rc = pthread_join ( threads [ t ] , NULL ) ; if ( rc ) { perror ( "Thread join error\n" ) ; } } return EXIT_SUCCESS ; } 