static int parse_probe_event ( const char * str ) { struct perf_probe_event * pev = & params . events [ params . nevents ] ; int ret ; pr_debug ( "probe-definition(%d): %s\n" , params . nevents , str ) ; if ( ++ params . nevents == MAX_PROBES ) { pr_err ( "Too many probes (>%d) were specified." , MAX_PROBES ) ; return - 1 ; } pev -> uprobes = params . uprobes ; if ( params . target ) { pev -> target = strdup ( params . target ) ; params . target_used = true ; } ret = parse_perf_probe_command ( str , pev ) ; pr_debug ( "%d arguments\n" , pev -> nargs ) ; return ret ; } 