static int search_cached_probe ( const char * target , const char * group , const char * event ) { struct probe_cache * cache = probe_cache__new ( target , NULL ) ; int ret ; if ( ! cache ) { pr_debug ( "Failed to open probe cache of %s\n" , target ) ; return - EINVAL ; } if ( ! probe_cache__find_by_name ( cache , group , event ) ) { pr_debug ( "Failed to find %s:%s in the cache\n" , group , event ) ; ret = - ENOENT ; } probe_cache__delete ( cache ) ; return ret ; } 