static KMF_RETURN copy_oid ( KMF_OID * dst , KMF_OID * src ) { KMF_RETURN ret = KMF_OK ; if ( dst == NULL || src == NULL ) { return ( KMF_ERR_BAD_PARAMETER ) ; } dst -> Data = malloc ( src -> Length , NULL ) ; if ( dst -> Data == NULL ) { return ( KMF_ERR_MEMORY ) ; } dst -> Length = src -> Length ; ( void ) memcpy ( dst -> Data , src -> Data , src -> Length ) ; return ( ret ) ; } 