rdma_stat rdma_clnt_postrecv ( CONN * conn , uint32_t xid ) { struct clist * cl = NULL ; rdma_stat retval ; rdma_buf_t rbuf = { 0 } ; rbuf . type = RECV_BUFFER ; if ( RDMA_BUF_ALLOC ( conn , & rbuf ) ) { return ( RDMA_NORESOURCE ) ; } clist_add ( & cl , 0 , rbuf . len , & rbuf . handle , rbuf . addr , NULL , NULL ) ; retval = RDMA_CLNT_RECVBUF ( conn , cl , xid ) ; return ( retval ) ; } 