static char * libpqrcv_identify_system ( WalReceiverConn * conn , TimeLineID * primary_tli ) { PGresult * res ; char * primary_sysid ; res = libpqrcv_PQexec ( conn -> streamConn , "IDENTIFY_SYSTEM" ) ; if ( PQresultStatus ( res ) != PGRES_TUPLES_OK ) { ereport ( ERROR , ( errcode ( ERRCODE_PROTOCOL_VIOLATION ) , errmsg ( "could not receive database system identifier and timeline ID from " "the primary server: %s" , pchomp ( PQerrorMessage ( conn -> streamConn ) ) ) ) ) ; } if ( PQnfields ( res ) < 3 || PQntuples ( res ) != 1 ) { int ntuples = PQntuples ( res ) ; int nfields = PQnfields ( res ) ; PQclear ( res ) ; ereport ( ERROR , ( errcode ( ERRCODE_PROTOCOL_VIOLATION ) , errmsg ( "invalid response from primary server" ) , errdetail ( "Could not identify system: got %d rows and %d fields, expected %d rows and %d or more fields." , ntuples , nfields , 3 , 1 ) ) ) ; } primary_sysid = pstrdup ( PQgetvalue ( res , 0 , 0 ) ) ; * primary_tli = pg_strtoint32 ( PQgetvalue ( res , 0 , 1 ) ) ; PQclear ( res ) ; return primary_sysid ; } 