static meta_entry_t * md_entry_clone_contents ( const meta_entry_t * orig ) { meta_entry_t * copy ; copy = md_entry_alloc ( orig -> key ) ; copy -> type = orig -> type ; if ( copy -> type == MD_TYPE_STRING ) { copy -> value . mv_string = strdup ( orig -> value . mv_string ) ; } else { copy -> value = orig -> value ; } return copy ; } 