Hotkey * isImmediateKey ( GWindow w , char * path , GEvent * event ) { char * wt = GDrawGetWindowTypeName ( w ) ; char * subMenuName = "_ImmediateKeys" ; char line [ PATH_MAX + 1 ] ; snprintf ( line , PATH_MAX , "%s.%s.%s" , wt , subMenuName , path ) ; Hotkey * hk = hotkeyFindByAction ( line ) ; if ( ! hk ) { return 0 ; } if ( ! hk -> action ) { return 0 ; } if ( event -> u . chr . keysym == hk -> keysym ) { return hk ; } return 0 ; } 