int mptcp_print ( netdissect_options * ndo , const u_char * cp , u_int len , u_char flags ) { const struct mptcp_option * opt ; u_int subtype ; ndo -> ndo_protocol = "mptcp" ; opt = ( const mptcp_option * ) cp ; subtype = MPTCP_OPT_SUBTYPE ( GET_U_1 ( opt -> sub_etc ) ) ; subtype = ND_MIN ( subtype , MPTCP_SUB_TCPRST + 1 ) ; ND_PRINT ( " %u" , len ) ; ND_PRINT ( " %s" , mptcp_options [ subtype ] . name ) ; return mptcp_options [ subtype ] . print ( ndo , cp , len , flags ) ; } 