static struct bp_tag * setup_commandline_tag ( struct bp_tag * params , char * cmdline ) { int len ; len = strlen ( cmdline ) ; params -> id = BP_TAG_COMMAND_LINE ; params -> size = ( len + 3 ) & - 4 ; strcpy ( ( char * ) params -> data , cmdline ) ; printf ( "   COMMAND_LINE:    tag:0x%04x, size:%u, data:'%s'\n" , BP_TAG_COMMAND_LINE , params -> size , cmdline ) ; return bp_tag_next ( params ) ; } 