int amd_pmf_get_pprof_modes ( struct amd_pmf_dev * pmf ) { int mode ; switch ( pmf -> current_profile ) { case PLATFORM_PROFILE_PERFORMANCE : mode = POWER_MODE_PERFORMANCE ; break ; case PLATFORM_PROFILE_BALANCED : mode = POWER_MODE_BALANCED_POWER ; break ; case PLATFORM_PROFILE_LOW_POWER : mode = POWER_MODE_POWER_SAVER ; break ; default : dev_err ( pmf -> dev , "Unknown Platform Profile.\n" ) ; return - EOPNOTSUPP ; } return mode ; } 