int pcmk_legacy2rc ( int legacy_rc ) { legacy_rc = abs ( legacy_rc , NULL ) ; switch ( legacy_rc ) { case pcmk_err_no_quorum : return pcmk_rc_no_quorum ; case pcmk_err_schema_validation : return pcmk_rc_schema_validation ; case pcmk_err_schema_unchanged : return pcmk_rc_schema_unchanged ; case pcmk_err_transform_failed : return pcmk_rc_transform_failed ; case pcmk_err_old_data : return pcmk_rc_old_data ; case pcmk_err_diff_failed : return pcmk_rc_diff_failed ; case pcmk_err_diff_resync : return pcmk_rc_diff_resync ; case pcmk_err_cib_modified : return pcmk_rc_cib_modified ; case pcmk_err_cib_backup : return pcmk_rc_cib_backup ; case pcmk_err_cib_save : return pcmk_rc_cib_save ; case pcmk_err_cib_corrupt : return pcmk_rc_cib_corrupt ; case pcmk_err_multiple : return pcmk_rc_multiple ; case pcmk_err_node_unknown : return pcmk_rc_node_unknown ; case pcmk_err_already : return pcmk_rc_already ; case pcmk_err_bad_nvpair : return pcmk_rc_bad_nvpair ; case pcmk_err_unknown_format : return pcmk_rc_unknown_format ; case pcmk_err_generic : return pcmk_rc_error ; case pcmk_ok : return pcmk_rc_ok ; default : return legacy_rc ; } } 