static int tw9910_set_scale ( struct i2c_client * client , const struct tw9910_scale_ctrl * scale ) { int ret ; ret = i2c_smbus_write_byte_data ( client , SCALE_HI , ( scale -> vscale & 0x0F00 ) >> 4 | ( scale -> hscale & 0x0F00 ) >> 8 ) ; ret = i2c_smbus_write_byte_data ( client , HSCALE_LO , scale -> hscale & 0x00FF ) ; if ( ret < 0 ) { return ret ; } ret = i2c_smbus_write_byte_data ( client , VSCALE_LO , scale -> vscale & 0x00FF ) ; return ret ; } 