static irqreturn_t lance_interrupt ( int irq , void * dev_id ) { struct net_device * dev = ( net_device * ) dev_id ; struct lance_private * lp = netdev_priv ( dev ) ; int csr0 ; spin_lock ( & lp -> devlock ) ; WRITERAP ( lp , LE_CSR0 ) ; csr0 = READRDP ( lp ) ; PRINT_RINGS ( ) ; if ( ! ( csr0 & LE_C0_INTR ) ) { spin_unlock ( & lp -> devlock ) ; return IRQ_NONE ; } WRITERDP ( lp , csr0 & ~ ( LE_C0_INEA | LE_C0_TDMD | LE_C0_STOP | LE_C0_STRT | LE_C0_INIT ) ) ; if ( ( csr0 & LE_C0_ERR ) ) { WRITERDP ( lp , LE_C0_BABL | LE_C0_ERR | LE_C0_MISS | LE_C0_INEA ) ; } if ( csr0 & LE_C0_RINT ) { lance_rx ( dev ) ; } if ( csr0 & LE_C0_TINT ) { lance_tx ( dev ) ; } if ( csr0 & LE_C0_BABL ) { dev -> stats . tx_errors ++ ; } if ( csr0 & LE_C0_MISS ) { dev -> stats . rx_errors ++ ; } if ( csr0 & LE_C0_MERR ) { printk ( "%s: Bus master arbitration failure, status %4.4x.\n" , dev -> name , csr0 ) ; WRITERDP ( lp , LE_C0_STRT ) ; } if ( lp -> tx_full && netif_queue_stopped ( dev ) && ( TX_BUFFS_AVAIL >= 0 ) ) { lp -> tx_full = 0 ; netif_wake_queue ( dev , NULL ) ; } WRITERAP ( lp , LE_CSR0 ) ; WRITERDP ( lp , LE_C0_BABL | LE_C0_CERR | LE_C0_MISS | LE_C0_MERR | LE_C0_IDON | LE_C0_INEA ) ; spin_unlock ( & lp -> devlock ) ; return IRQ_HANDLED ; } 