static int ffa_device_match ( struct device * dev , struct device_driver * drv ) { const struct ffa_device_id * id_table ; struct ffa_device * ffa_dev ; id_table = to_ffa_driver ( drv ) -> id_table ; ffa_dev = to_ffa_dev ( dev , NULL ) ; while ( ! uuid_is_null ( & id_table -> uuid ) ) { if ( uuid_is_null ( & ffa_dev -> uuid ) ) { ffa_device_match_uuid ( ffa_dev , & id_table -> uuid ) ; } if ( uuid_equal ( & ffa_dev -> uuid , & id_table -> uuid ) ) { return 1 ; } id_table ++ ; } return 0 ; } 