static void handle_internal_command ( int argc , const char * * argv ) { const char * cmd = argv [ 0 ] ; unsigned int i ; if ( argc > 1 && ! strcmp ( argv [ 1 ] , "--help" ) ) { argv [ 1 ] = argv [ 0 ] ; argv [ 0 ] = cmd = "help" ; } for ( i = 0 ; i < ARRAY_SIZE ( commands ) ; i ++ ) { struct cmd_struct * p = commands + i ; if ( strcmp ( p -> cmd , cmd ) ) { continue ; } exit ( run_builtin ( p , argc , argv ) ) ; } } 