static int kempld_wdt_set_timeout ( struct watchdog_device * wdd , unsigned int timeout ) { struct kempld_wdt_data * wdt_data = watchdog_get_drvdata ( wdd ) ; struct kempld_wdt_stage * pretimeout_stage ; struct kempld_wdt_stage * timeout_stage ; int ret ; timeout_stage = & wdt_data -> stage [ STAGE_TIMEOUT ] ; pretimeout_stage = & wdt_data -> stage [ STAGE_PRETIMEOUT ] ; if ( pretimeout_stage -> mask && wdt_data -> pretimeout > 0 ) { timeout = wdt_data -> pretimeout ; } ret = kempld_wdt_set_stage_action ( wdt_data , timeout_stage , ACTION_RESET ) ; ret = kempld_wdt_set_stage_timeout ( wdt_data , timeout_stage , timeout ) ; if ( ret ) { return ret ; } wdd -> timeout = timeout ; return 0 ; } 