cmsInterpFunction DefaultInterpolatorsFactory ( cmsUInt32Number nInputChannels , cmsUInt32Number nOutputChannels , cmsUInt32Number dwFlags ) { cmsInterpFunction Interpolation ; cmsBool IsFloat = ( dwFlags & CMS_LERP_FLAGS_FLOAT ) ; cmsBool IsTrilinear = ( dwFlags & CMS_LERP_FLAGS_TRILINEAR ) ; if ( nInputChannels >= 4 && nOutputChannels >= MAX_STAGE_CHANNELS ) { return Interpolation ; } switch ( nInputChannels ) { case 1 : if ( nOutputChannels == 1 ) { if ( IsFloat ) { Interpolation . LerpFloat = LinLerp1Dfloat ; } else { Interpolation . Lerp16 = LinLerp1D ; } } else { if ( IsFloat ) { Interpolation . LerpFloat = Eval1InputFloat ; } else { Interpolation . Lerp16 = Eval1Input ; } } break ; case 2 : if ( IsFloat ) { Interpolation . LerpFloat = BilinearInterpFloat ; } else { Interpolation . Lerp16 = BilinearInterp16 ; } break ; case 3 : if ( IsTrilinear ) { if ( IsFloat ) { Interpolation . LerpFloat = TrilinearInterpFloat ; } else { Interpolation . Lerp16 = TrilinearInterp16 ; } } else { if ( IsFloat ) { Interpolation . LerpFloat = TetrahedralInterpFloat ; } else { Interpolation . Lerp16 = TetrahedralInterp16 ; } } break ; case 4 : if ( IsFloat ) { Interpolation . LerpFloat = Eval4InputsFloat ; } else { Interpolation . Lerp16 = Eval4Inputs ; } break ; case 5 : if ( IsFloat ) { Interpolation . LerpFloat = Eval5InputsFloat ; } else { Interpolation . Lerp16 = Eval5Inputs ; } break ; case 6 : if ( IsFloat ) { Interpolation . LerpFloat = Eval6InputsFloat ; } else { Interpolation . Lerp16 = Eval6Inputs ; } break ; case 7 : if ( IsFloat ) { Interpolation . LerpFloat = Eval7InputsFloat ; } else { Interpolation . Lerp16 = Eval7Inputs ; } break ; case 8 : if ( IsFloat ) { Interpolation . LerpFloat = Eval8InputsFloat ; } else { Interpolation . Lerp16 = Eval8Inputs ; } break ; case 9 : if ( IsFloat ) { Interpolation . LerpFloat = Eval9InputsFloat ; } else { Interpolation . Lerp16 = Eval9Inputs ; } break ; case 10 : if ( IsFloat ) { Interpolation . LerpFloat = Eval10InputsFloat ; } else { Interpolation . Lerp16 = Eval10Inputs ; } break ; case 11 : if ( IsFloat ) { Interpolation . LerpFloat = Eval11InputsFloat ; } else { Interpolation . Lerp16 = Eval11Inputs ; } break ; case 12 : if ( IsFloat ) { Interpolation . LerpFloat = Eval12InputsFloat ; } else { Interpolation . Lerp16 = Eval12Inputs ; } break ; case 13 : if ( IsFloat ) { Interpolation . LerpFloat = Eval13InputsFloat ; } else { Interpolation . Lerp16 = Eval13Inputs ; } break ; case 14 : if ( IsFloat ) { Interpolation . LerpFloat = Eval14InputsFloat ; } else { Interpolation . Lerp16 = Eval14Inputs ; } break ; case 15 : if ( IsFloat ) { Interpolation . LerpFloat = Eval15InputsFloat ; } else { Interpolation . Lerp16 = Eval15Inputs ; } break ; default : Interpolation . Lerp16 = NULL ; } return Interpolation ; } 