u64 gf_audio_fmt_get_layout_from_name ( const char * name ) { u32 i , iname , nb_cicp = sizeof ( GF_CICPLayouts ) / sizeof ( GF_CICPAudioLayout ) ; iname = atoi ( name ) ; for ( i = 0 ; i < nb_cicp ; i ++ ) { if ( ! strcmp ( GF_CICPLayouts [ i ] . name , name ) ) { return GF_CICPLayouts [ i ] . channel_mask ; } if ( GF_CICPLayouts [ i ] . cicp == iname ) { return GF_CICPLayouts [ i ] . channel_mask ; } } GF_LOG ( GF_LOG_WARNING , GF_LOG_CORE , ( "Unsupported audio layout name %s\n" , name ) ) ; return 0 ; } 