BOOL input_register_client_callbacks ( rdpInput * input ) { rdpSettings * settings ; if ( ! input -> context ) { return FALSE ; } settings = input -> context -> settings ; if ( freerdp_settings_get_bool ( settings , FreeRDP_FastPathInput ) ) { input -> SynchronizeEvent = input_send_fastpath_synchronize_event ; input -> KeyboardEvent = input_send_fastpath_keyboard_event ; input -> KeyboardPauseEvent = input_send_fastpath_keyboard_pause_event ; input -> UnicodeKeyboardEvent = input_send_fastpath_unicode_keyboard_event ; input -> MouseEvent = input_send_fastpath_mouse_event ; input -> ExtendedMouseEvent = input_send_fastpath_extended_mouse_event ; input -> FocusInEvent = input_send_fastpath_focus_in_event ; } else { input -> SynchronizeEvent = input_send_synchronize_event ; input -> KeyboardEvent = input_send_keyboard_event ; input -> KeyboardPauseEvent = input_send_keyboard_pause_event ; input -> UnicodeKeyboardEvent = input_send_unicode_keyboard_event ; input -> MouseEvent = input_send_mouse_event ; input -> ExtendedMouseEvent = input_send_extended_mouse_event ; input -> FocusInEvent = input_send_focus_in_event ; } return TRUE ; } 