static struct brcmf_core * brcmf_chip_add_core ( struct brcmf_chip_priv * ci , u16 coreid , u32 base , u32 wrapbase ) { struct brcmf_core_priv * core ; core = kmalloc ( sizeof ( * core ) , GFP_KERNEL ) ; if ( ! core ) { return ERR_PTR ( - ENOMEM ) ; } core -> pub . id = coreid ; core -> pub . base = base ; core -> chip = ci ; core -> wrapbase = wrapbase ; list_add_tail ( & core -> list , & ci -> cores ) ; return & core -> pub ; } 