void tcp_reass_timer ( void * arg ) { conn_t * connp = ( conn_t * ) arg ; tcp_t * tcp = connp -> conn_tcp ; if ( tcp -> tcp_reass_head == NULL ) { return ; } ASSERT ( tcp -> tcp_reass_tail != NULL ) ; if ( tcp -> tcp_snd_sack_ok && tcp -> tcp_num_sack_blk > 0 ) { tcp_sack_remove ( tcp -> tcp_sack_list , TCP_REASS_END ( tcp -> tcp_reass_tail ) , & tcp -> tcp_num_sack_blk ) ; } tcp_close_mpp ( & tcp -> tcp_reass_head ) ; tcp -> tcp_reass_tail = NULL ; TCP_STAT ( tcp -> tcp_tcps , tcp_reass_timeout ) ; } 