bool get_config_bool ( const config_t * cfg , bool * result ) { if ( ! strcasecmp ( cfg -> value , "yes" ) ) { * result = true ; return true ; } if ( ! strcasecmp ( cfg -> value , "no" ) ) { * result = false ; return true ; } logger ( DEBUG_ALWAYS , LOG_ERR , "\"yes\" or \"no\" expected for configuration variable %s in %s line %d" , cfg -> variable , cfg -> file , cfg -> line ) ; return false ; } 