void M_sql_connpool_set_timeouts ( M_sql_connpool_t * pool , M_time_t reconnect_time_s , M_time_t max_idle_time_s , M_time_t fallback_s ) { M_thread_mutex_lock ( pool -> lock ) ; if ( reconnect_time_s >= 0 ) { pool -> reconnect_time_s = reconnect_time_s ; } if ( max_idle_time_s >= 0 ) { pool -> max_idle_time_s = max_idle_time_s ; } if ( fallback_s >= 0 ) { pool -> fallback_s = fallback_s ; } M_thread_mutex_unlock ( pool -> lock ) ; } 