void M_fs_perms_set_dir_mode ( M_fs_perms_t * perms , M_uint32 mode , M_fs_perms_who_t who , M_fs_perms_type_t type ) { switch ( who ) { case M_FS_PERMS_WHO_USER : perms -> dir_user_set = M_TRUE ; perms -> dir_user_mode = mode ; perms -> dir_user_type = type ; break ; case M_FS_PERMS_WHO_GROUP : perms -> dir_group_set = M_TRUE ; perms -> dir_group_mode = mode ; perms -> dir_group_type = type ; break ; case M_FS_PERMS_WHO_OTHER : perms -> dir_other_set = M_TRUE ; perms -> dir_other_mode = mode ; perms -> dir_other_type = type ; break ; } } 